mapper.select:
		select id,article_id,sort,reply_id,create_user,gmt_create,view_times,reply_times,upvote_times,stepon_times,content from article_reply
		where 1=1
            AND id = #{obj.search.id}
            AND article_id = #{obj.search.articleId}
            AND sort = #{obj.search.sort}
            AND reply_id = #{obj.search.replyId}
            AND create_user = #{obj.search.createUser}
            AND gmt_create = #{obj.search.gmtCreate}
            AND view_times = #{obj.search.viewTimes}
            AND reply_times = #{obj.search.replyTimes}
            AND upvote_times = #{obj.search.upvoteTimes}
            AND stepon_times = #{obj.search.steponTimes}
            AND content = #{obj.search.content}

         order by ${obj.orderField} ${obj.orderType}

		select id,article_id,sort,reply_id,create_user,gmt_create,view_times,reply_times,upvote_times,stepon_times,content from article_reply
		where 1=1
            AND id = #{obj.id}
            AND article_id = #{obj.articleId}
            AND sort = #{obj.sort}
            AND reply_id = #{obj.replyId}
            AND create_user = #{obj.createUser}
            AND gmt_create = #{obj.gmtCreate}
            AND view_times = #{obj.viewTimes}
            AND reply_times = #{obj.replyTimes}
            AND upvote_times = #{obj.upvoteTimes}
            AND stepon_times = #{obj.steponTimes}
            AND content = #{obj.content}

mapper.update:
update article_reply
id=#{obj.id}, article_id=#{obj.articleId}, sort=#{obj.sort}, reply_id=#{obj.replyId}, create_user=#{obj.createUser}, gmt_create=#{obj.gmtCreate}, view_times=#{obj.viewTimes}, reply_times=#{obj.replyTimes}, upvote_times=#{obj.upvoteTimes}, stepon_times=#{obj.steponTimes}, content=#{obj.content},
where id=#{obj.id} AND version = #{versionId}
