mapper.select:
		select id,create_user,gmt_create,gmt_modified,content,title,labels,view_times,reply_times,upvote_times,stepon_times,categories from blog_article
		where 1=1
            AND id = #{obj.search.id}
            AND create_user = #{obj.search.createUser}
            AND gmt_create = #{obj.search.gmtCreate}
            AND gmt_modified = #{obj.search.gmtModified}
            AND content = #{obj.search.content}
            AND title = #{obj.search.title}
            AND labels = #{obj.search.labels}
            AND view_times = #{obj.search.viewTimes}
            AND reply_times = #{obj.search.replyTimes}
            AND upvote_times = #{obj.search.upvoteTimes}
            AND stepon_times = #{obj.search.steponTimes}
            AND categories = #{obj.search.categories}

         order by ${obj.orderField} ${obj.orderType}

		select id,create_user,gmt_create,gmt_modified,content,title,labels,view_times,reply_times,upvote_times,stepon_times,categories from blog_article
		where 1=1
            AND id = #{obj.id}
            AND create_user = #{obj.createUser}
            AND gmt_create = #{obj.gmtCreate}
            AND gmt_modified = #{obj.gmtModified}
            AND content = #{obj.content}
            AND title = #{obj.title}
            AND labels = #{obj.labels}
            AND view_times = #{obj.viewTimes}
            AND reply_times = #{obj.replyTimes}
            AND upvote_times = #{obj.upvoteTimes}
            AND stepon_times = #{obj.steponTimes}
            AND categories = #{obj.categories}

mapper.update:
update blog_article
id=#{obj.id}, create_user=#{obj.createUser}, gmt_create=#{obj.gmtCreate}, gmt_modified=#{obj.gmtModified}, content=#{obj.content}, title=#{obj.title}, labels=#{obj.labels}, view_times=#{obj.viewTimes}, reply_times=#{obj.replyTimes}, upvote_times=#{obj.upvoteTimes}, stepon_times=#{obj.steponTimes}, categories=#{obj.categories},
where id=#{obj.id} AND version = #{versionId}
