mapper.select:
		select id,name,nick_name,picture,mobile,email,password,real_name from user
		where 1=1
            AND name = #{obj.search.name}
            AND nick_name = #{obj.search.nickName}
            AND picture = #{obj.search.picture}
            AND mobile = #{obj.search.mobile}
            AND email = #{obj.search.email}
            AND password = #{obj.search.password}
            AND real_name = #{obj.search.realName}

         order by ${obj.orderField} ${obj.orderType}

		select id,name,nick_name,picture,mobile,email,password,real_name from user
		where 1=1
            AND name = #{obj.name}
            AND nick_name = #{obj.nickName}
            AND picture = #{obj.picture}
            AND mobile = #{obj.mobile}
            AND email = #{obj.email}
            AND password = #{obj.password}
            AND real_name = #{obj.realName}

mapper.update:
update user
name=#{obj.name}, nick_name=#{obj.nickName}, picture=#{obj.picture}, mobile=#{obj.mobile}, email=#{obj.email}, password=#{obj.password}, real_name=#{obj.realName},
where id=#{obj.id} AND version = #{versionId}
